import React, { Component } from "react";
import Child from "./Child";
class Parent extends Component {
  state = {
    name: "",
    age: "",
    children: [
      {
        name: "Caleb",
        age: 40
      },
      {
        name: "Rob",
        age: 55
      }
    ]
  };

  createChild = () => {
    let newChild = { name: this.state.name, age: this.state.age };
    let newChildren = [...this.state.children];
    newChildren.push(newChild);
    this.setState({ children: newChildren });
  };

  nameChanged = ev => {
    let name = ev.target.value;
    this.setState({ name: name });
  };

  ageChanged = ev => {
    let age = ev.target.value;
    this.setState({ age: age });
  };

  render() {
    return (
      <div>
        <button onClick={this.createChild}>Add Child</button>
        <input placeholder={"name"} onChange={this.nameChanged} />
        <input onChange={this.ageChanged} />
        This is my child
        {this.state.children.map(child => (
          <Child name={child.name} age={child.age} />
        ))}
      </div>
    );
  }
}

export default Parent;
